import 'package:cloud_firestore/cloud_firestore.dart';import 'package:flutter/material.dart';import 'package:grocery/ui/home/home.dart';import 'package:grocery/ui/sign_in.dart';import 'package:grocery/widgets/fade_in.dart';class CheckUserActivity extends StatelessWidget {  final id;  const CheckUserActivity({Key key, this.id}) : super(key: key);  userInfo() async {    print("get user");    final FirebaseFirestore _db = FirebaseFirestore.instance;    var user = await _db.collection("users").doc(id).get();    print("user here");    return user;  }  @override  Widget build(BuildContext context) {    return Scaffold(      body: FutureBuilder(        future: userInfo(),        builder: (BuildContext context, AsyncSnapshot<dynamic> snapshot) {          try {            if (snapshot.hasData) {              // UserProfile _up = UserProfile.fromJson(snapshot.data);              if (snapshot.connectionState == ConnectionState.waiting) {                return Center(                  child: CircularProgressIndicator(),                );              } else {                String active = snapshot.data["active"];                print(active);                if (active == 'active') {                  return FadeIn(child: Home.create(context));                }                if (active == 'not active') {                  return Center(                    child: Text("you have been banned."),                  );                } else                  return Center(                    child: Text("an unknown error occurred."),                  );              }            } else              return Text("");          } catch (e) {            return FadeIn(child: SignIn.create(context));          }        },      ),    );  }}